    const updateFormValues = (e, field) => {
        const val = e.target.value;
        updateForm({...formValues, [field] : val});
    }
    return (
        <form>
            <input type="text" placeholder="title" value={formValues.title} onChange={(e) => updateFormValues(e, 'title')}/>
