import { v1 as uuid } from 'uuid';

export const BookReducer = (state, action) => {
    switch(action.type) {
        case 'ADD_TODO':
        return [...state, {title: action.book.title, author: action.book.author, id: uuid()}];
        break;

        case 'REMOVE_TODO':
        return state.filter((item) => item.id !== action.id);
        break;

        case 'default':
        return state;
        break;
    }
}
